mapper.select:
select i.item_id as id, i.name, i.price, i.start_time as startTime, i.end_time as endTime, i.status, img.url as imgUrl,
-- Mine table case when m.like_id is not null and m.status = 1 then 1 else 0 end as isLiked
from Item i
-- Join Image img join Image img on i.item_id = img.item_id and img.seq = 0
-- Join Mine table left join Mine m on i.item_id = m.item_id and m.user_id = #{userId}
-- Join Category table join Category c on i.code = c.code and c.name = #{category}
order by case when i.status in ('NOT_OPEN', 'OPEN') then 0 else 1 end, i.end_time asc;
